import React from "react";
import { ConstructorElement,
    DragIcon, } from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burger-list.module.css";



const BurgerList = ({bun, filling}) => {
    return (
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <ConstructorElement
          type="top"
          isLocked={true}
          text= {bun.name + " " + "(верх)"}
          price={200}
          thumbnail={img}
        />

        {filling.map((arr) => {
           <ConstructorElement
              text={arr.name}
              price={arr.price}
              thumbnail={arr.img}
              key={arr.id}
           />  
        })}


        <ConstructorElement
          type="bottom"
          isLocked={true}
          text={bun.name + " " + "(низ)"}
          price={200}
          thumbnail={img}
        />
      </div>
    )
  }

  export default BurgerList;
