import React from "react";
import { ConstructorElement,
    DragIcon, } from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burger-list.module.css";
import {data} from "../../utils/data";

// const bunArr = data.filter(e => {e.type === 'bun'});
const bunArr = data;
console.log(bunArr.filter((el) => el.type === "bun"));


const BurgerList = ({img, name}) => {
    return (
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <ConstructorElement
          type="top"
          isLocked={true}
          text="Краторная булка N-200i (верх)"
          price={200}
          thumbnail={img}
        />
        <ConstructorElement
          text="Краторная булка N-200i (верх)"
          price={50}
          thumbnail={img}
        />
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text="Краторная булка N-200i (низ)"
          price={200}
          thumbnail={img}
        />
      </div>
    )
  }

  export default BurgerList;
