import React from "react";
import { ConstructorElement,
    DragIcon, } from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burger-list.module.css";



const BurgerList = ({img, name, price, filling, id}) => {
    return (
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <ConstructorElement
          type="top"
          isLocked={true}
          text= "Краторная булка N-200i (верх)"
          price={200}
          thumbnail={img}
        />

        {filling.map((arr) => {
           <ConstructorElement
              text="Краторная булка N-200i (верх)"
              price={50}
              thumbnail={img}
           />  
        })}
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text="Краторная булка N-200i (низ)"
          price={200}
          thumbnail={img}
        />
      </div>
    )
  }

  export default BurgerList;
