import React from "react";
import { ConstructorElement,
    DragIcon, } from "@ya.praktikum/react-developer-burger-ui-components";
import styles from "./burger-list.module.css";

const BurgerList = ({img, name}) => {
    return (
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        <ConstructorElement
          type="top"
          isLocked={true}
          text="Краторная булка N-200i (верх)"
          price={200}
          thumbnail={img}
          extraClass="ml-8"
        />
        <div className="stles.list">
        <div>
            <DragIcon type="primary" />
            <ConstructorElement
               text="Краторная булка N-200i (верх)"
               price={50}
               thumbnail={img}
            />
        </div>
        <div>
            <DragIcon type="primary" />
            <ConstructorElement
               text="Краторная булка N-200i (верх)"
               price={50}
               thumbnail={img}
            />
        </div>
        <div>
            <DragIcon type="primary" />
            <ConstructorElement
               text="Краторная булка N-200i (верх)"
               price={50}
               thumbnail={img}
            />
        </div>
        <div>
            <DragIcon type="primary" />
            <ConstructorElement
               text="Краторная булка N-200i (верх)"
               price={50}
               thumbnail={img}
            />
        </div>
        </div>
        <ConstructorElement
          type="bottom"
          isLocked={true}
          text="Краторная булка N-200i (низ)"
          price={200}
          thumbnail={img}
          extraClass="ml-8"
        />
      </div>
    )
  }

  export default BurgerList;
